        <!-- Example row of columns -->
        <div class=\"row\">
            {% for post in allPosts %}
          <div class=\"col-md-3\">
            <h2>
                {{post.title |u.truncate(100,'...') }}
            </h2>
            <p>
                {{post.content| u.truncate(100,'...') }}
            </p>
            <p>
            <span class=\"text-muted\">
